mapper.update:
  	INSERT INTO
  	tcoin_market(
  	symbol
  	,refer_currency
  	,origin
  	,last_price
  	,bid
  	,ask
  	,mid
  	,low
  	,high
  	,volume
  	,update_datetime
  	,coinmarketcap_id
  	)
  	VALUE(
  	#{symbol,jdbcType=VARCHAR}
  	 ,#{referCurrency,jdbcType=VARCHAR}
  	 ,#{origin,jdbcType=VARCHAR}
  	 ,#{lastPrice,jdbcType=DECIMAL}
  	 ,#{bid,jdbcType=DECIMAL}
  	 ,#{ask,jdbcType=DECIMAL}
  	 ,#{mid,jdbcType=DECIMAL}
  	 ,#{low,jdbcType=DECIMAL}
  	 ,#{high,jdbcType=DECIMAL}
  	 ,#{volume,jdbcType=VARCHAR}
  	 ,#{updateDatetime,jdbcType=TIMESTAMP}
  	 ,#{coinmarketcapId,jdbcType=VARCHAR}
  	)
   	on DUPLICATE key update
   	symbol = #{symbol,jdbcType=VARCHAR},
    refer_currency = #{referCurrency,jdbcType=VARCHAR},
    origin = #{origin,jdbcType=VARCHAR},
    last_price = #{lastPrice,jdbcType=DECIMAL},
    bid = #{bid,jdbcType=DECIMAL},
    ask = #{ask,jdbcType=DECIMAL},
    mid = #{mid,jdbcType=DECIMAL},
    low = #{low,jdbcType=DECIMAL},
    high = #{high,jdbcType=DECIMAL},
    volume = #{volume,jdbcType=VARCHAR},
    update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
	coinmarketcap_id = #{coinmarketcapId,jdbcType=VARCHAR}
